from Bio.SeqUtils import GC

stop_codons = ['TGA', 'TAA', 'TAG']

def has_stops(seq):
    for i in range(0, len(seq), 3):
        if seq[i:i+3] in stop_codons:
            return True

    return False


gfp = ""
gfp_gc = GC(gfp)

min_distance = 100.0

kan = ""

lowest_i = -1

for start_i in range(len(kan) - 714):
    kan_sequence = kan[start_i:start_i+714]
    kan_gc = GC(kan_sequence)
    if abs(kan_gc - gfp_gc) < min_distance and not has_stops('G' + kan_sequence):
        min_distance = abs(kan_gc - gfp_gc)
        lowest_i = start_i
        print(start_i, min_distance)

print(kan[lowest_i:lowest_i+714])

